}
.logo {
  font-family: 'druk';
  font-size: 1.2em;
  font-weight: 600;
}
:root {
  --primary: #fff;
  --secondary: #000;
  --side-nav-width: 18vw;
  --stripe-height: 40vh;
  --main-width: calc(100% - var(--side-nav-width));
  --deco-bar: calc(var(--bar) / 3);
  --bar: 80px;
  --pad: var(--deco-bar);
  --sect-40-width: calc(var(--side-nav-width) * 2);
}
* {
  color: var(--secondary);
}
h1 {
  font-family: 'druk';
}
* {
  box-sizing: border-box;
  text-decoration: none;
  list-style: none;
  text-transform: uppercase;
}
html,
body {
  margin: 0;
  padding: 0;
  font-family: 'oswald';
  overflow-x: hidden;
}
body {
  width: 100%;
  min-height: 100vh;
}
a,
ul,
li {
  text-decoration: none;
  list-style: none;
  margin: 0;
  padding: 0;
}
.bottom {
  bottom: 0;
}
.top {
  top: 0;
}
.left {
  bottom: 0;
}
.right {
  right: 0;
}
.inner {
  width: 100%;
  height: 2em;
  background-color: var(--test-3);
  align-self: center;
}
.inner--flex {
  display: flex;
  justify-content: space-between;
}
.inner * {
