}
.split {
  position: absolute;
  width: 60px;
  height: 3px;
  top: calc(50vh - 9px);
  margin-left: var(--pad);
  box-shadow: 0 -3px 0 0 var(--primary), 0 3px 0 0 var(--primary), 0 9px 0 0 var(--primary);
  transform: rotate(0);
}
.main {
  position: absolute;
  right: 0;
  width: var(--main-width);
  height: 100%;
}
.head {
  height: 100vh;
}
section {
  width: 100%;
  height: 100vh;
  background-color: #f00;
}
.sect-40 {
  width: calc(var(--side-nav-width) * 2);
  height: 100%;
  display: flex;
}
.sect-40::after {
  content: '';
  position: absolute;
  left: calc(var(--side-nav-width) * 2);
  right: var(--pad);
  top: calc(100% - var(--deco-bar) * 3);
  bottom: 0;
  background-color: var(--secondary);
  z-index: 2;
