  position: relative;
  display: flex;
  align-items: center;
}
.hori .text {
  font-weight: 400;
  color: var(--primary);
}
.hori::before {
  content: '';
  position: absolute;
  width: 50%;
  height: 100%;
  background-color: var(--secondary);
  z-index: -1;
}
.sect-60 {
  position: absolute;
  width: calc(100% - var(--sect-40-width) - var(--pad));
  top: var(--pad);
  right: var(--pad);
  bottom: var(--pad);
